import sailpoint.object.*;
import sailpoint.api.ObjectUtil;

success = false;
message = "Unknown error";

// 1. Validate Credentials using HTTP Loopback (like connection-form.tsx)
// We use standard Java HttpURLConnection in Beanshell
try {
    String targetUrl = "http://localhost:8080/identityiq/scim/v2/ServiceProviderConfig";
    java.net.URL url = new java.net.URL(targetUrl);
    java.net.HttpURLConnection conn = (java.net.HttpURLConnection) url.openConnection();
    conn.setRequestMethod("GET");

    // Construct Basic Auth Header manually
    String auth = identityName + ":" + password;
    String encodedAuth = java.util.Base64.getEncoder().encodeToString(auth.getBytes("UTF-8"));
    conn.setRequestProperty("Authorization", "Basic " + encodedAuth);
    conn.setRequestProperty("Content-Type", "application/scim+json");
    conn.setRequestProperty("Accept", "application/scim+json, application/json");

    int status = conn.getResponseCode();

    if (status != 200) {
         workflow.put("success", "false");
         workflow.put("message", "Invalid Password (HTTP " + status + ")");
         return;
    }
} catch (Exception e) {
     workflow.put("success", "false");
     workflow.put("message", "Auth Error: " + e.getMessage());
     return;
}

// 2. Fetch Identity (Validated)
Identity identity = context.getObjectByName(Identity.class, identityName);
if (identity == null) {
    workflow.put("success", "false");
    workflow.put("message", "Identity not found");
    return;
}

int channelIdx = 0; // Default WA
try {
    if (channel != null) channelIdx = Integer.parseInt(channel);
} catch (Exception e) {}

// 3. Update PHONE Attribute (WA,TG Format)
String currentVal = identity.getStringAttribute("phone");
System.out.println("[SailSetu] Registering " + (channelIdx == 0 ? "WhatsApp" : "Telegram") + " ID: " + phoneNumber + " for " + identityName);
System.out.println("[SailSetu] Current phone attribute: " + currentVal);

String waPart = "";
String tgPart = "";

if (currentVal != null && currentVal.length() > 0) {
    if (currentVal.contains(",")) {
        String[] existing = currentVal.split(",");
        if (existing.length > 0) waPart = existing[0].trim();
        if (existing.length > 1) tgPart = existing[1].trim();
    } else {
        // Single value exists. 
        // If we are registering Telegram, assume existing is WhatsApp.
        // If we are registering WhatsApp, assume existing is WhatsApp (to be replaced).
        if (channelIdx == 1) {
             waPart = currentVal.trim();
        } else {
             waPart = currentVal.trim(); // This will be replaced below anyway
        }
    }
}

if (channelIdx == 0) {
    waPart = phoneNumber;
} else {
    tgPart = phoneNumber;
}

String newVal = waPart + "," + tgPart;
System.out.println("[SailSetu] New phone attribute value: " + newVal);
identity.setAttribute("phone", newVal);

context.saveObject(identity);
context.commitTransaction();

// Also maintain Custom Object Map for fast lookups (backup)
String customName = "SailSetuPhoneMappings";
Custom custom = context.getObjectByName(Custom.class, customName);
if (custom == null) {
    custom = new Custom();
    custom.setName(customName);
     custom.setAttributes(new Attributes());
}
custom.put(phoneNumber, identityName);
context.saveObject(custom);
context.commitTransaction();

workflow.put("success", "true");
workflow.put("message", "Identity Verified! ID " + phoneNumber + " linked to " + identity.getDisplayName());
